select count(*) from apply_check a 
	inner join order_table b on a.OrderId = b.OrderId
	left join user c on a.ApplyCheckUserId = c.UserId 
	left join user d on a.ApplyCheckBackUserId = d.UserId

	
		
			and a.ApplyCheckId = #{applycheckid,jdbcType=VARCHAR}
		
		
			and a.OrderId = #{orderid,jdbcType=VARCHAR}
		
		
			and a.ApplyCheckState = #{applycheckstate,jdbcType=VARCHAR}
		
		
			and a.ApplyCheckState != "HG" and a.ApplyCheckState != "SQYS"
		
		
			and a.ApplyCheckBackFault = #{applycheckbackfault,jdbcType=VARCHAR}
		
		
			and a.ApplyCheckUserId = #{applycheckuserid,jdbcType=VARCHAR}
		
		
			and a.ApplyCheckTime >= #{selectapplycheckstarttime,jdbcType=VARCHAR} 
			and a.ApplyCheckTime <= #{selectapplycheckendtime,jdbcType=VARCHAR} 
		
		
			and a.ApplyCheckBackUserId = #{applycheckbackuserid,jdbcType=VARCHAR}
		
		
			and a.ApplyCheckBackTime >= #{selectapplycheckbackstarttime,jdbcType=VARCHAR} 
			and a.ApplyCheckBackTime <= #{selectapplycheckbackendtime,jdbcType=VARCHAR} 
		
		
			and b.BuildingTypeId = #{buildingtypeid,jdbcType=VARCHAR}
		
		
			and b.OrderPlanTime >= #{selectorderplanstarttime,jdbcType=VARCHAR} 
			and b.OrderPlanTime <= #{selectorderplanendtime,jdbcType=VARCHAR} 
		
		
			and b.OrderBiddingEndTime >= #{selectorderbiddingendstarttime,jdbcType=VARCHAR} 
			and b.OrderBiddingEndTime <= #{selectorderbiddingendendtime,jdbcType=VARCHAR} 
		
		
			and b.OrderType = #{ordertype,jdbcType=VARCHAR}
		
		
			and b.OrderState = #{orderstate,jdbcType=VARCHAR}
		
		
			and b.OrderServiceUserId = #{orderserviceuserid,jdbcType=VARCHAR}
		
		
