select count(*) from order_table a
	left join building_type b on a.BuildingTypeId = b.BuildingTypeId 

	
		
			and a.OrderId = #{orderid,jdbcType=VARCHAR}
		
		
			and a.BuildingTypeId = #{buildingtypeid,jdbcType=VARCHAR}
		
		
			and a.OrderPlanTime >= #{selectstarttime,jdbcType=VARCHAR} 
			and a.OrderPlanTime <= #{selectendtime,jdbcType=VARCHAR} 
		
		
			and a.OrderBiddingEndTime >= #{selectorderbiddingendstarttime,jdbcType=VARCHAR} 
			and a.OrderBiddingEndTime <= #{selectorderbiddingendendtime,jdbcType=VARCHAR} 
		
		
			and a.OrderType = #{ordertype,jdbcType=VARCHAR}
		
		
			and a.OrderState = #{orderstate,jdbcType=VARCHAR}
		
		
			and a.OrderServiceUserId = #{orderserviceuserid,jdbcType=VARCHAR}
		
		
			and a.OrderApplyCheckUserId = #{orderapplycheckuserid,jdbcType=VARCHAR}
		
		
			and b.BuildingTypeState = #{buildingtypestate,jdbcType=VARCHAR}
		
		
			and a.OrderCheckAllState = #{ordercheckallstate,jdbcType=VARCHAR}
		
		
			and a.OrderPosition like CONCAT(#{orderposition,jdbcType=VARCHAR},'%')
		
		
			and (a.OrderProjectName like CONCAT('%',#{orderprojectname,jdbcType=VARCHAR},'%')
		
		
			or a.OrderContactPerson like CONCAT('%',#{ordercontactperson,jdbcType=VARCHAR},'%')
		
		
			or a.OrderContactPhone like CONCAT('%',#{ordercontactphone,jdbcType=VARCHAR},'%')
		
		
			or a.OrderAddress like CONCAT('%',#{orderaddress,jdbcType=VARCHAR},'%'))

select * from order_table a 
	left join building_type b on a.BuildingTypeId = b.BuildingTypeId
	left join property_company c on a.OrderPropertyCompanyId = c.PropertyCompanyId

	
		
			and a.OrderId = #{orderid,jdbcType=VARCHAR}
		
		
			and a.BuildingTypeId = #{buildingtypeid,jdbcType=VARCHAR}
		
		
			and a.OrderPlanTime >= #{selectstarttime,jdbcType=VARCHAR} 
			and a.OrderPlanTime <= #{selectendtime,jdbcType=VARCHAR} 
		
		
			and a.OrderBiddingEndTime >= #{selectorderbiddingendstarttime,jdbcType=VARCHAR} 
			and a.OrderBiddingEndTime <= #{selectorderbiddingendendtime,jdbcType=VARCHAR} 
		
		
			and a.OrderType = #{ordertype,jdbcType=VARCHAR}
		
		
			and a.OrderState = #{orderstate,jdbcType=VARCHAR}
		
		
			and a.OrderServiceUserId = #{orderserviceuserid,jdbcType=VARCHAR}
		
		
			and a.OrderApplyCheckUserId = #{orderapplycheckuserid,jdbcType=VARCHAR}
		
		
			and b.BuildingTypeState = #{buildingtypestate,jdbcType=VARCHAR}
		
		
			and a.OrderCheckAllState = #{ordercheckallstate,jdbcType=VARCHAR}
		
		
			and a.OrderPosition like CONCAT(#{orderposition,jdbcType=VARCHAR},'%')
		
		
			and (a.OrderProjectName like CONCAT('%',#{orderprojectname,jdbcType=VARCHAR},'%')
		
		
			or a.OrderContactPerson like CONCAT('%',#{ordercontactperson,jdbcType=VARCHAR},'%')
		
		
			or a.OrderContactPhone like CONCAT('%',#{ordercontactphone,jdbcType=VARCHAR},'%')
		
		
			or a.OrderAddress like CONCAT('%',#{orderaddress,jdbcType=VARCHAR},'%'))
		
	



	limit #{pagenumber,jdbcType=INTEGER}, #{pagesize,jdbcType=INTEGER}

		SELECT count(*) FROM
		order_table ot
		LEFT JOIN order_detail od ON ot.OrderId = od.OrderId
		left JOIN goods g on od.GoodsId = g.GoodsId
		left JOIN project p on od.ProjectId = p.ProjectId
		
			

				
					and ot.OrderCreateUserId = #{ordercreateuserid,jdbcType=VARCHAR}
				
				
					and ot.OrderServiceUserId = #{orderserviceuserid,jdbcType=VARCHAR}
				
				
					and ot.OrderState = #{orderstate,jdbcType=VARCHAR}
				
				
					and ot.OrderPosition = #{orderposition,jdbcType=VARCHAR}

		SELECT
		*
		FROM
		order_table ot
		LEFT JOIN order_detail od ON ot.OrderId = od.OrderId
		left JOIN goods g on od.GoodsId = g.GoodsId
		left JOIN project p on od.ProjectId = p.ProjectId
		
			

				
					and ot.OrderCreateUserId = #{ordercreateuserid,jdbcType=VARCHAR}
				
				
					and ot.OrderServiceUserId = #{orderserviceuserid,jdbcType=VARCHAR}
				
				
					and ot.OrderState = #{orderstate,jdbcType=VARCHAR}
				
				
					and ot.OrderPosition = #{orderposition,jdbcType=VARCHAR}
				

			
		
		order by OrderCreateTime desc
		
		
			limit #{pagenumber,jdbcType=INTEGER}, #{pagesize,jdbcType=INTEGER}

