select count(*) from snatch a 
	inner join order_table b on a.OrderId = b.OrderId
	left join user c on a.ServiceUserId = c.UserId
	left join user d on a.SnatchConfirmUserId = d.UserId

	
		
			and a.SnatchId = #{snatchid,jdbcType=VARCHAR}
		
		
			and a.OrderId = #{orderid,jdbcType=VARCHAR}
		
		
			and a.ServiceUserId = #{serviceuserid,jdbcType=VARCHAR}
		
		
			and a.SnatchState = #{snatchstate,jdbcType=VARCHAR}
		
		
			and a.SnatchEnrollTime >= #{selectsnatchenrollstarttime,jdbcType=VARCHAR} 
			and a.SnatchEnrollTime <= #{selectsnatchenrollendtime,jdbcType=VARCHAR} 
		
		
			and a.SnatchConfirmUserId = #{snatchconfirmuserid,jdbcType=VARCHAR}
		
		
			and a.SnatchConfirmTime >= #{selectsnatchconfirmstarttime,jdbcType=VARCHAR} 
			and a.SnatchConfirmTime <= #{selectsnatchconfirmendtime,jdbcType=VARCHAR} 
		
		
			and b.BuildingTypeId = #{buildingtypeid,jdbcType=VARCHAR}
		
		
			and b.OrderPlanTime >= #{selectorderplanstarttime,jdbcType=VARCHAR} 
			and b.OrderPlanTime <= #{selectorderplanendtime,jdbcType=VARCHAR} 
		
		
			and b.OrderBiddingEndTime >= #{selectorderbiddingendstarttime,jdbcType=VARCHAR} 
			and b.OrderBiddingEndTime <= #{selectorderbiddingendendtime,jdbcType=VARCHAR} 
		
		
			and b.OrderType = #{ordertype,jdbcType=VARCHAR}
		
		
			and b.OrderState = #{orderstate,jdbcType=VARCHAR}
		
		
			and b.OrderServiceUserId = #{orderserviceuserid,jdbcType=VARCHAR}
		
		
			and b.OrderApplyCheckUserId = #{orderapplycheckuserid,jdbcType=VARCHAR}

