select * from user_payment

  
    
      and UserPaymentId = #{userpaymentid,jdbcType=VARCHAR}
    
    
      and OrderId = #{orderid,jdbcType=VARCHAR}
    
    
      and RemitterUserId = #{remitteruserid,jdbcType=VARCHAR}
    
  

