    select count(*) from user_payment up
    inner join order_table ot on up.OrderId = ot.OrderId
    left join user u on up.RemitterUserId = u.UserId
    left join user u2 on ot.OrderServiceUserId = u2.UserId
    
      
        
          and up.UserPaymentId = #{userpaymentid,jdbcType=VARCHAR}
        
        
          and up.OrderId = #{orderid,jdbcType=VARCHAR}
        
        
          and up.RemitterUserId = #{remitteruserid,jdbcType=VARCHAR}
        
        
          and up.UserPaymentCreateTime >= #{paymentstarttime,jdbcType=VARCHAR} 
          and up.UserPaymentCreateTime <= #{paymentendtime,jdbcType=VARCHAR} 
        

        
          or ot.OrderType = #{ordertype,jdbcType=VARCHAR}
        

        
          and ot.OrderProjectType = #{orderprojecttype,jdbcType=VARCHAR}
        
        
          and ot.OrderState = #{orderstate,jdbcType=VARCHAR}
        
        
          and up.UserPaymentOther1 = #{userpaymentother1,jdbcType=VARCHAR}
        
        
          and up.OrderRemark = #{orderremark,jdbcType=VARCHAR}
        
        
          and up.UserPaymentPlatformServiceState = #{userpaymentplatformservicestate,jdbcType=VARCHAR}
        
        
          and (up.UserPaymentPlatformAccount like CONCAT('%',#{userpaymentplatformaccount,jdbcType=VARCHAR},'%')
        
        
          or up.UserPaymentServiceAccount like CONCAT('%',#{userpaymentplatformaccount,jdbcType=VARCHAR},'%')
        
        
          or up.UserPaymentPlatformAccountName like CONCAT('%',#{userpaymentplatformaccountname,jdbcType=VARCHAR},'%')
        
        
          or ot.OrderProjectName like CONCAT('%',#{orderprojectname,jdbcType=VARCHAR},'%'))
        
        
        
          or u.UserRealName like CONCAT('%',#{customeruser_userrealname,jdbcType=VARCHAR},'%')
        
        
        
          or u2.UserRealName like CONCAT('%',#{serviceuser_userrealname,jdbcType=VARCHAR},'%')

    select up.UserPaymentId ,up.OrderId,up.OrderProjectName,up.OrderPriceTotalMoney,up.IdentificationCode,up.RemitterUserId,up.UserPaymentCreateUserId,up.UserPaymentCreateTime,
    up.UserPaymentUpdateUserId, up.UserPaymentUpdateTime,up.UserPaymentOther1,up.UserPaymentOther2,up.UserPaymentOther3,up.OrderRemark,up.UserPaymentPlatformServiceState,up.UserPaymentPlatformAccount,
    up.UserPaymentServiceAccount,up.UserPaymentPlatformAccountName,ot.OrderAddress,ot.OrderPlanTime,ot.OrderBiddingEndTime,
    ot.OrderProjectType,ot.OrderInterceptTotalMoney,ot.OrderInterceptTravelTotalMoney,ot.OrderPriceTotalMoney OrderPriceTotalMoney1,ot.OrderTravelTotalMoney,ot.OrderType,ot.OrderState,
    ot.OrderCreateUserId,u.UserRealName,u.UserPhone,u.UserRole,u.UserAccount,u.UserAccountAddress,u2.UserRealName UserRealName1,u2.UserPhone UserPhone1,u2.UserRole UserRole1,
      u2.UserAccount UserAccount1,u2.UserAccountAddress UserAccountAddress1 from user_payment up
    inner join order_table ot on up.OrderId = ot.OrderId
    left join user u on up.RemitterUserId = u.UserId
    left join user u2 on ot.OrderServiceUserId = u2.UserId
    
      
        
          and up.UserPaymentId = #{userpaymentid,jdbcType=VARCHAR}
        
        
          and up.OrderId = #{orderid,jdbcType=VARCHAR}
        
        
          and up.RemitterUserId = #{remitteruserid,jdbcType=VARCHAR}
        
        
          and up.UserPaymentCreateTime >= #{paymentstarttime,jdbcType=VARCHAR} 
          and up.UserPaymentCreateTime <= #{paymentendtime,jdbcType=VARCHAR} 
        

        
          or ot.OrderType = #{ordertype,jdbcType=VARCHAR}
        

        
          and ot.OrderProjectType = #{orderprojecttype,jdbcType=VARCHAR}
        
        
          and ot.OrderState = #{orderstate,jdbcType=VARCHAR}
        
        
          and up.UserPaymentOther1 = #{userpaymentother1,jdbcType=VARCHAR}
        
        
          and up.OrderRemark = #{orderremark,jdbcType=VARCHAR}
        
        
          and up.UserPaymentPlatformServiceState = #{userpaymentplatformservicestate,jdbcType=VARCHAR}
        
        
          and (up.UserPaymentPlatformAccount like CONCAT('%',#{userpaymentplatformaccount,jdbcType=VARCHAR},'%')
        
        
          or up.UserPaymentServiceAccount like CONCAT('%',#{userpaymentplatformaccount,jdbcType=VARCHAR},'%')
        
        
          or up.UserPaymentPlatformAccountName like CONCAT('%',#{userpaymentplatformaccountname,jdbcType=VARCHAR},'%')
        
        
